import React, { useState } from "react";
import ExpensesContainer from "./components/ExpensesContainer.jsx";
import NewExpense from "./components/NewExpense/NewExpense.jsx";

const EXPENSES = [];

const App = () => {
  const [expenses, setExpenses] = useState(EXPENSES);

  const newExpenseHandler = (newExpense) => {
    console.log(`This is the new expense added:`);
    setExpenses((prevExpenses) => {
      [newExpense, ...prevExpenses];
    });
  };
  return (
    <div>
      <NewExpense onGetNewExpense={newExpenseHandler} />
      <ExpensesContainer expensesList={expenses} />
    </div>
  );
};

export default App;
