import React, { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import "../styles/NewExpense.css";

const NewExpense = () => {
  const [numberOfClicks, setClick] = useState(0);
  const clickHandler = () =>
    setClick(numberOfClicks >= 9 ? "OK THATS ENOUGH" : numberOfClicks + 1);
  return (
    <div className="new-expense">
      <ExpenseForm />
      <button onClick={clickHandler}>{numberOfClicks}</button>
    </div>
  );
};

export default NewExpense;
