import React, { useState } from "react";
import "../styles/ExpenseForm.css";

const ExpenseForm = (props) => {
  const [titleInput, setTitleInput] = useState("");
  const [amountInput, setAmountInput] = useState("");
  const [dateInput, setDateInput] = useState("");

  // const [userInput, setUserInput] = useState({
  //   titleInput: "",
  //   amountInput: "",
  //   dateInput: "",
  // });

  const changeTitleHandler = (e) => {
    setTitleInput(e.target.value);
    // setUserInput((prevState) => {
    //   return {
    //     ...prevState,
    //     setTitleInput: `${e.target.value}`,
    //   };
    // });
    // console.log(setUserInput.setTitleInput);
  };
  const changeAmountHandler = (e) => {
    setAmountInput(e.target.value);
    // setUserInput({
    //   ...userInput,
    //   setAmountInput: e.target.value,
    // });
    // console.log(setUserInput.setAmountInput);
  };
  const changeDateHandler = (e) => {
    setDateInput(e.target.value);
    // setUserInput({
    //   ...userInput,
    //   setDateInput: e.target.value,
    // });
    // console.log(setUserInput.setDateInput);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    //Aqui se cria um objeto que irá armazenar toda a informação inputada pelo usuário, usando a state variable atualizada
    const expenseData = {
      title: titleInput,
      amount: amountInput,
      date: new Date(dateInput),
    };
    expenseDataHandler(expenseData);
    setTitleInput("");
    setDateInput("");
    setAmountInput("");
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" value={titleInput} onChange={changeTitleHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={changeAmountHandler}
            value={amountInput}
          />
        </div>
        <div className="new-expense__control">
          <label>Date </label>
          <input
            type="date"
            min="2019-01-01"
            max="2023-12-31"
            onChange={changeDateHandler}
            value={dateInput}
          />
        </div>
        <div className="new-expense__actions">
          <button>Add Expense</button>
        </div>
      </div>
    </form>
  );
};

export default ExpenseForm;
